import React from "react";

function Note(props) {
  const [area, setArea] = useState([]);

  function addNote(newNote) {
    setArea((prevValue) => {
      return [...prevValue, newNote];
    });
  }

  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p>{props.content}</p>
      <button>DELETE</button>
    </div>
  );
}

export default Note;
